mapper.update:
update tbl_reply
set delFlag = true, replyText='삭제된 댓글입니다.', replyer='unknown'
where rno = #{rno}

update tbl_reply
set replyText = #{replyText}, updateDate = now()
where rno = #{rno}

mapper.select:
select * from tbl_reply
where bno = #{bno}
and rno > 0
order by rno desc
limit #{limit} offset #{skip}

select count(rno) from tbl_reply
where bno = #{bno}
and rno > 0
